    function getCurrentTimeDate() {
      let today = Date.now();
      let todayDate = new Date(today);
      let date = todayDate.toDateString();
      let time = todayDate.toLocaleTimeString("en-US");
      let currentTimeDate = `${time} - ${date}`;
      return currentTimeDate;
    }

